import React from "react";
import { StatusBar } from "expo-status-bar";
import {
  Alert,
  Keyboard,
  StyleSheet,
  SafeAreaView,
  View,
  Text,
  TextInput,
  TouchableOpacity,
} from "react-native";
import { Icon, withTheme } from "@rneui/themed";
import { LinearGradient } from "expo-linear-gradient";
import response from "../services/userService";
import COLORS from "../components/Colors";
import Button from "../components/Button";
import Input from "../components/Input";
import AsyncStorage from "@react-native-async-storage/async-storage";
//import Icon from 'react-native-vector-icons/MaterialCommunityIcons'

const Login = ({ navigation }) => {
  const [inputs, setInputs] = React.useState({ usuario: "jiep17", contraseña: "12345678" });
  const [errors, setErrors] = React.useState({});
  const [loading, setLoading] = React.useState(false);

  const validate = async () => {
    Keyboard.dismiss();
    let isValid = true;
    if (!inputs.usuario) {
      handleError("Ingrese su usuario", "usuario");
      isValid = false;
    }
    if (!inputs.contraseña) {
      handleError("Ingrese su contraseña", "contraseña");
      isValid = false;
    } else if (inputs.contraseña.length < 8) {
      handleError("La longitud mínima de la constraseña es 8", "contraseña");
      isValid = false;
    }

    if (isValid) {
      login();
    }
  };

  const login = () => {
    //setLoading(true);
    setTimeout(async () => {
      setLoading(false);
      let userData = await AsyncStorage.getItem("userData");
      if (userData) {
        userData = JSON.parse(userData);
        if (
          inputs.usuario == userData.usuario &&
          inputs.contraseña == userData.contraseña
        ) {
          navigation.navigate("Inicio");
          AsyncStorage.setItem(
            "userData",
            JSON.stringify({ ...userData, loggedIn: true })
          );
        } else {
          Alert.alert(
            "Error",
            "Datos ingresados incorrectos"
          );
        }
      } else {
        Alert.alert("Error", "User does not exist");
      }
    }, 10);
  };

  const handleOnchange = (text, input) => {
    setInputs((prevState) => ({ ...prevState, [input]: text }));
  };

  const handleError = (error, input) => {
    setErrors((prevState) => ({ ...prevState, [input]: error }));
  };

  return (
    <SafeAreaView style={{ backgroundColor: withTheme, flex: 1 }}>
      <View style={{ paddingTop: 50, paddingHorizontal: 20 }}>
        <Text style={{ color: withTheme, fontSize: 40, fontWeight: "bold" }}>
          MINSAPP
        </Text>

        <View style={{ marginVertical: 20 }}>
          <Input
            onChangeText={(text) => handleOnchange(text, "usuario")}
            onFocus={() => handleError(null, "usuario")}
            iconName="account-circle"
            label="Usuario"
            placeholder="Ingresa tu usuario"
            error={errors.usuario}
          />
          <Input
            onChangeText={(text) => handleOnchange(text, "contraseña")}
            onFocus={() => handleError(null, "contraseña")}
            iconName="lock-outline"
            label="Contraseña"
            placeholder="Ingresa tu contraseña"
            error={errors.contraseña}
            password
          />
          <Button title="Entrar" onPress={validate} />
          <Text
            onPress={() => navigation.navigate("Registro")}
            style={{
              color: COLORS.black,
              fontWeight: "bold",
              textAlign: "center",
              fontSize: 16,
            }}
          >
            ¿Aún no tienes una cuenta? Registrate
          </Text>
        </View>
      </View>
    </SafeAreaView>
  );
};

export default Login;
