import AsyncStorage from "@react-native-async-storage/async-storage";
import React from "react";
import {
  View,
  Text,
  SafeAreaView,
  Keyboard,
  ScrollView,
  Alert,
} from "react-native";

import COLORS from "../components/Colors";
import Button from "../components/Button";
import Input from "../components/Input";
import _Appbar from "../components/Appbar";
import Constants from 'expo-constants'

const BusquedaHistoriaClinica = ({ navigation }) => {
  const [inputs, setInputs] = React.useState({
    nombre: "",
    dni: ""
  });

  const [errors, setErrors] = React.useState({});
  const [loading, setLoading] = React.useState(false);

  const validate = () => {
    Keyboard.dismiss();
    let isValid = true;

    if (!inputs.nombre) {
      handleError("Ingresa tu nombre", "nombre");
      isValid = false;
    }

    if (!inputs.dni) {
      handleError("Ingresa tu DNI.", "dni");
      isValid = false;
    } else if (inputs.dni.length != 8) {
      handleError("La longitud del DNI. es 8", "dni");
      isValid = false;
    }

    if (isValid) {
      buscar();
    }
  };

  const buscar = () => {
    // Creo una variable igual que para mostrar la wea del appvar y ya esta
  }

  const handleOnchange = (text, input) => {
    setInputs((prevState) => ({ ...prevState, [input]: text }));
  };
  const handleError = (error, input) => {
    setErrors((prevState) => ({ ...prevState, [input]: error }));
  };

  return (
    <View style={{marginTop: Constants.statusBarHeight, flexGrow:1}}>
        <_Appbar {...navigation}/>
        <SafeAreaView style={{ backgroundColor: COLORS.white, flex: 1 }}>
        <ScrollView
            contentContainerStyle={{ paddingTop: 50, paddingHorizontal: 20 }}
        >
            <Text style={{ color: COLORS.black, fontSize: 40, fontWeight: "bold" }}>
            BÚSQUEDA DE HISTORIA CLÍNICA
            </Text>
            <View style={{ marginVertical: 20 }}>
            <Input
                onChangeText={(text) => handleOnchange(text, "nombre")}
                onFocus={() => handleError(null, "nombre")}
                iconName="account-circle"
                label="Apellidos y Nombres"
                //placeholder="Ingresa tus nombres"
                error={errors.nombre}
            />

            <Input
                keyboardType="numeric"
                onChangeText={(text) => handleOnchange(text, "dni")}
                onFocus={() => handleError(null, "dni")}
                iconName="card-account-details-outline"
                label="DNI"
                //placeholder="Enter your phone no"
                error={errors.dni}
            />

            <Button title="Buscar" onPress={validate} />
            </View>
        </ScrollView>
        </SafeAreaView>
    </View>
  );
};

export default BusquedaHistoriaClinica;
